set path=pSys.Location
// Check Status - Must be committed at this point do pTrace.AddCheckpoint("Checking Status") set os=##class(CFDSource.Util.OSCommand).%New(pTrace, path, "git", "status") set sc=os.Run() quit:$$$ISERR(sc) sc do ..ProcessGITResponse(os.Output, .tBranch, .staged, .committed, .tItems, pTrace) quit:'committed $$$ERROR("There are uncommitted/staged local changes, you may have work outstanding. Rectify manually or Export system")
// Switch to develop if tBranch'="develop" { do pTrace.AddCheckpoint("Switching to [develop]") // TODO: Need to handle this quit:tBranch'="develop" $$$ERROR("Not currently on [develop] branch, you may have work outstanding. Rectify manually") }
// Pull in from remote if pPull=1 { do pTrace.AddCheckpoint("Pulling from remote repository to get latest") set os=##class(CFDSource.Util.OSCommand).%New(pTrace, path, "git", "pull origin develop") set sc=os.Run() quit:$$$ISERR(sc) sc
do pTrace.AddCheckpoint("Checking Status") set os=##class(CFDSource.Util.OSCommand).%New(pTrace, path, "git", "status") set sc=os.Run() quit:$$$ISERR(sc) sc do ..ProcessGITResponse(os.Output, .tBranch, .staged, .committed, .tItems, pTrace) quit:'committed $$$ERROR("There are merges that need to be reconciled, you may have work outstanding. Rectify manually") }
// Create Feature Branch do pTrace.AddCheckpoint("Creating Feature Branch") set os=##class(CFDSource.Util.OSCommand).%New(pTrace, path, "git", "checkout -b "_pFeature_" develop") set sc=os.Run() quit:$$$ISERR(sc) sc
do pTrace.AddCheckpoint("Checking Status") set os=##class(CFDSource.Util.OSCommand).%New(pTrace, path, "git", "status") set sc=os.Run() quit:$$$ISERR(sc) sc do ..ProcessGITResponse(os.Output, .tBranch, .staged, .committed, .tItems, pTrace) quit:tBranch'=pFeature $$$ERROR("Unable to create/switch to branch, branch is now: "_tBranch_" (try git status on command line)")
quit $$$OK

set path=pSys.Location
// Check Status - Must be committed at this point do pTrace.AddCheckpoint("Checking Status") set os=##class(CFDSource.Util.OSCommand).%New(pTrace, path, "git", "status") set sc=os.Run() quit:$$$ISERR(sc) sc do ..ProcessGITResponse(os.Output, .tBranch, .staged, .committed, .tItems, pTrace) quit:'committed $$$ERROR("There are uncommitted/staged local changes, you may have work outstanding. Rectify manually or Export system")
// Switch branch do pTrace.AddCheckpoint("Moving to feature branch: "_pFeature) set os=##class(CFDSource.Util.OSCommand).%New(pTrace, path, "git", "checkout "_pFeature) set sc=os.Run() quit:$$$ISERR(sc) sc
quit $$$OK

set path=pSys.Location
// Check Status - Must be committed at this point do pTrace.AddCheckpoint("Checking Status") set os=##class(CFDSource.Util.OSCommand).%New(pTrace, path, "git", "status") set sc=os.Run() quit:$$$ISERR(sc) sc do ..ProcessGITResponse(os.Output, .tBranch, .staged, .committed, .tItems, pTrace) quit:'committed $$$ERROR("There are uncommitted/staged local changes, you may have work outstanding. Rectify manually or Export system")
// Go back to [develop] do pTrace.AddCheckpoint("Moving to main [develop] branch") set os=##class(CFDSource.Util.OSCommand).%New(pTrace, path, "git", "checkout develop") set sc=os.Run() quit:$$$ISERR(sc) sc
// Apply all changes from feature do pTrace.AddCheckpoint("Applying Feature") set os=##class(CFDSource.Util.OSCommand).%New(pTrace, path, "git", "merge --no-ff "_tBranch) set sc=os.Run() quit:$$$ISERR(sc) sc
// Check Status (ensure all ok) do pTrace.AddCheckpoint("Checking Status") set os=##class(CFDSource.Util.OSCommand).%New(pTrace, path, "git", "status") set sc=os.Run() quit:$$$ISERR(sc) sc do ..ProcessGITResponse(os.Output, .tBranch, .staged, .committed, .tItems, pTrace) quit:'committed $$$ERROR("There are merges (or other errors) that need to be reconciled, you may have work outstanding. Suggest ExportSource with a commit comment")
// Push to remote if pPush { do pTrace.AddCheckpoint("Pushing to origin") set os=##class(CFDSource.Util.OSCommand).%New(pTrace, path, "git", "push origin develop") set sc=os.Run() quit:$$$ISERR(sc) sc }
quit $$$OK

set path=pSys.Location
// Check status - might not need to commit do pTrace.AddCheckpoint("Checking Status") set os=##class(CFDSource.Util.OSCommand).%New(pTrace, path, "git", "status") set sc=os.Run() quit:$$$ISERR(sc) sc do ..ProcessGITResponse(os.Output, .tBranch, .staged, .committed, .tItems, pTrace)
do pTrace.AddPOI("Status checked, we are on ["_tBranch_"] branch and we are "_$S(committed:"[Committed]", 1:"[Uncommitted]")) quit:committed $$$OK
// Stage Changes do pTrace.AddCheckpoint("Staging Changes") set added=0 set k=$O(tItems("")) while k'="" { if tItems(k)="deleted" { set os=##class(CFDSource.Util.OSCommand).%New(pTrace, path, "git", "rm "_tItems(k, "path")) set sc=os.Run() quit:$$$ISERR(sc) } else { set added=1 } set k=$O(tItems(k)) } quit:$$$ISERR(sc) sc
do pTrace.AddCheckpoint("Staging new items") set os=##class(CFDSource.Util.OSCommand).%New(pTrace, path, "git", "add .") set sc=os.Run() quit:$$$ISERR(sc) sc
// Commit do pTrace.AddCheckpoint("Committing") set os=##class(CFDSource.Util.OSCommand).%New(pTrace, path, "git", "commit -m '"_$ZSTRIP(pMessage, "*", "'")_"'") set sc=os.Run() quit:$$$ISERR(sc) sc
// Check! do pTrace.AddCheckpoint("Checking Status") set os=##class(CFDSource.Util.OSCommand).%New(pTrace, path, "git", "status") set sc=os.Run() quit:$$$ISERR(sc) sc do ..ProcessGITResponse(os.Output, .tBranch, .staged, .committed, .tItems, pTrace)
do pTrace.AddPOI("Commit completed, we are on ["_tBranch_"] branch and we are "_$S(committed:"[Committed]", 1:"[Uncommitted]")) quit:'committed $$$ERROR("Could not commited local directory, review status")
